import { Chambre } from './../../model/Chambre';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ChambreService } from 'src/app/services/chambre.service';
@Component({
  selector: 'app-show-chambre',
  templateUrl: './show-chambre.component.html',
  styleUrls: ['./show-chambre.component.scss'],
})
export class ShowChambreComponent implements OnInit {
  chambres: any[] = [];
  constructor(private sChambre: ChambreService, private router: Router) {}
  ngOnInit(): void {
    this.sChambre.getAllChambre().subscribe((data: any) => {
      console.log(data);
      this.chambres = data;
    });
  }
  showDetails(idChambre: any) {
    this.router.navigate(['/detailCh', idChambre]);
  }
  editCh(idChambre: any) {
    this.router.navigate(['/updateCh', idChambre]);
    // console.log("update");
  }
  deleteCh(ch: Chambre) {
    this.sChambre.deleteChambre(ch).subscribe((data) => {
      console.log(data);
    });
  }
  addChambre() {
    this.router.navigate(['addch']); // Naviguer vers la page d'ajout
  }

}
<button class="btn btn-danger btn-sm action-button" (click)="addChambre()">
  Ajouter chambre
</button>

<table class="table table-striped table-bordered">
  <thead>
    <tr>
      <th>ID</th>
      <th>Numéro de Chambre</th>
      <th>Type de Chambre</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let ch of chambres">
      <td>{{ ch.idChambre }}</td>
      <td>{{ ch.numeroChambre }}</td>
      <td>
        <span *ngIf="ch.typeChambre" class="badge badge-info">{{ ch.typeChambre }}</span>
      </td>
      <td>
        <button class="btn btn-primary btn-sm action-button" (click)="showDetails(ch.idChambre)">
          <i class="fas fa-info-circle"></i> Détails
        </button>
        <button class="btn btn-warning btn-sm action-button" (click)="editCh(ch.idChambre)">
          <i class="fas fa-edit"></i> Modifier
        </button>
        <button class="btn btn-danger btn-sm action-button" (click)="deleteCh(ch)">
          <i class="fas fa-trash-alt"></i> Supprimer
        </button>
      </td>
    </tr>
  </tbody>
</table>
