import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ReservationsService } from 'src/app/services/reservations.service';

@Component({
  selector: 'app-add-reservation',
  templateUrl: './add-reservation.component.html',
  styleUrls: ['./add-reservation.component.scss']
})
export class AddReservationComponent {
  addRes: FormGroup;


  constructor(private fb: FormBuilder, private sReservation: ReservationsService) {
    this.addRes = this.fb.group({
      idReservation: ['', Validators.required],
      anneeUniversite: ['', Validators.required],
      estValid: [''],
      commentaire:['',Validators.required]
    });
  }

  onSubmit() {
    if (this.addRes.valid) {
      const reservation = this.addRes.value;
      this.sReservation.addReservation(reservation).subscribe((data) => {
        console.log(data);
        alert('reservation ajoutée avec succès');
      });
    }
  }
}
<form [formGroup]="addRes" (ngSubmit)="onSubmit()" class="form-container">

  <div class="mb-3">
    <label for="id" class="form-label">ID de la réservation:</label>
    <input type="text" id="id" formControlName="idReservation" class="form-control">
  </div>

  <div class="mb-3">
    <label for="dateInput" class="form-label">Année Universitaire:</label>
    <input type="date" id="dateInput" formControlName="anneeUniversite" class="form-control">
  </div>

  <div class="mb-3">
    <label class="form-label">Est Valide?</label>
    <div class="form-check">
      <input type="radio" id="trueValue" formControlName="estValid" [value]="false" class="form-check-input">
      <label class="form-check-label" for="trueValue">Non</label>

    </div>
    <div class="form-check"><input type="radio" id="falseValue"  formControlName="estValid" [value]="true" class="form-check-input">
      <label class="form-check-label" for="trueValue">oui</label>
    </div>
  </div>

  <div class="mb-3">
    <label for="comment" class="form-label">Commentaire :</label>
    <textarea id="comment" formControlName="commentaire" rows="4" class="form-control"></textarea>
  </div>

  <button type="submit" class="btn btn-primary">Ajouter la réservation</button>
</form>
