import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-detail-reservation',
  templateUrl: './detail-reservation.component.html',
  styleUrls: ['./detail-reservation.component.scss']
})
export class DetailReservationComponent implements OnInit {
  reservations: any;
  constructor(
    private route: ActivatedRoute,
    private sChambre: ChambreService
  ) {}
  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      const idParam = params.get('idChambre');
      if (idParam !== null) {
        const chambreId = +idParam;
        this.sChambre.getChambreById(chambreId).subscribe(chambre => {
          this.chambre = chambre;
        });
      } else {

      }
    });
  }
}
