import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ChambreService } from 'src/app/services/chambre.service';

@Component({
  selector: 'app-show-chambre',
  templateUrl: './show-chambre.component.html',
  styleUrls: ['./show-chambre.component.scss'],
})
export class ShowChambreComponent implements OnInit {
  chambres: any[] = [];
  constructor(private sChambre: ChambreService, private router: Router) {}
  ngOnInit(): void {
    this.sChambre.getAllChambre().subscribe((data: any) => {
      console.log(data);
      this.chambres = data;
    });
  }
  showDetails(id:any){
    this.router.navigate(['detailCh',id]);

  }
  editCh(id: any) {
    this.router.navigate(['updateCh', id]);
    console.log("update");
  }
  test(){

  }
}
<table class="table table-striped table-bordered">
  <thead>
    <tr>
      <th>ID</th>
      <th>Numéro de Chambre</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let ch of chambres">
      <td>{{ ch.idChambre }}</td>
      <td>{{ ch.numeroChambre }}</td>
      <td>
        <button class="btn btn-primary btn-sm action-button">
          <i class="fas fa-info-circle" (click)="showDetails(ch.id)"></i> Détails
        </button>
        <input type="button" value="up" (click)="showDetails(ch.id)"/>
        <button class="btn btn-warning btn-sm action-button">
          <i class="fas fa-edit" (click)="editCh(ch.id)"></i> Modifier
        </button>
        <button class="btn btn-danger btn-sm action-button">
          <i class="fas fa-trash-alt"></i> Supprimer
        </button>
      </td>
    </tr>
  </tbody>
</table>
