import { HttpHeaders, HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Chambre } from '../model/Chambre';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ChambreService {
  private url: String = 'http://localhost:8082';
  httpOptions = {
    headers: new HttpHeaders({ 'Content-Type': 'application/Json' }),
  };
  constructor(private http:HttpClient) { }
  getAllChambre(){
    return this.http.get(this.url+'/allChambre');
  }
  addChambre(chambre:any){
    return this.http.post(this.url+'/chambres',chambre);
  }
  deleteChambre(ch:Chambre):Observable<Chambre>{
    return this.http.delete<Chambre>(this.url+'/chambres/'+ch.idChambre);

  }
  getChambreById(id:any){
    return this.http.get(this.url+'/chambres/'+id);
  }
  updateChambre(id:any,chambre:Chambre): Observable<Chambre> {
    return this.http.put<Chambre>(this.url+'/chambres/'+id,chambre);
  }
}
