import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ReservationsService } from 'src/app/services/reservations.service';

@Component({
  selector: 'app-add-reservation',
  templateUrl: './add-reservation.component.html',
  styleUrls: ['./add-reservation.component.scss']
})
export class AddReservationComponent {
  addRes: FormGroup;


  constructor(private fb: FormBuilder, private sReservation: ReservationsService) {
    this.addRes = this.fb.group({

      anneeUniversite: ['', Validators.required],
      estValid: ['', Validators.required],
      commentaire:['',Validators.required]
    });
  }

  onSubmit() {
    if (this.addRes.valid) {
      const reservation = this.addRes.value;
      this.sReservation.addReservation(reservation).subscribe((data) => {
        console.log(data);
        alert('reservation ajoutée avec succès');
      });
    }
  }
}
<form [formGroup]="addRes" (ngSubmit)="onSubmit()" class="form-container">
  <div class="form-group">
    <label  class="form-label">Annee Universitaire:</label>
    <input type="date" name="dateInput" id="dateInput">

  </div>
  <div class="form-group">
    <label  class="form-label">Est Valide?</label>
    <input type="radio" name="boolValue" id="trueValue" value="true">
  </div>
  <label for="comment">Commentaire :</label>
  <textarea id="comment" name="comment" rows="4" cols="50"></textarea>


  <button type="submit" class="btn btn-primary">Ajouter la reservation</button>
</form>
