import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ChambreService } from 'src/app/services/chambre.service';
import { TypeChambre } from 'src/app/model/typeChambre';
import { Router } from '@angular/router';

@Component({
  selector: 'app-add-chambre',
  templateUrl: './add-chambre.component.html',
  styleUrls: ['./add-chambre.component.scss'],
})
export class AddChambreComponent {
  addCh: FormGroup;
  typeChambre = TypeChambre;

  constructor(private fb: FormBuilder, private chService: ChambreService , private router:Router) {
    this.addCh = this.fb.group({
      numeroChambre: ['', Validators.required],

      typeC: ['', Validators.required], // Initialisez à null
    });
  }

  onSubmit() {
    if (this.addCh.valid) {
      const ch = this.addCh.value;
      this.chService.addChambre(ch).subscribe((data) => {
        console.log(data);
        alert('Chambre ajoutée avec succès');
        this.router.navigate(['//allch']);

      });
    }
  }
}
<form [formGroup]="addCh" (ngSubmit)="onSubmit()" class="form-container">
  <div class="form-group">
    <label for="numeroChambre" class="form-label">Numéro de Chambre:</label>
    <input type="number" id="numeroChambre" formControlName="numeroChambre" class="form-control"/>
  </div>
  <div  class="form-group">
    <label for="typeChambre" class="form-label">Type de Chambre:</label>
    <select id="typeChambre" formControlName="typeC" class="form-control">
      <option *ngFor="let type of typeChambre | keyvalue" [value]="type.value">{{ type.value }}</option>
    </select>
  </div>

  <button type="submit" class="btn btn-primary" [disabled]="addCh.invalid">Ajouter Chambre</button>
</form>
