import { ReservationsService } from 'src/app/services/reservations.service';
import { Reservations } from './../../model/Reservations';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-show-reservation',
  templateUrl: './show-reservation.component.html',
  styleUrls: ['./show-reservation.component.scss']
})
export class ShowReservationComponent implements OnInit{
reservations:any[]=[];
constructor(private sReservation:ReservationsService,private router:Router){}
ngOnInit(): void {
  this.sReservation.getAllReservations().subscribe((data:any)=>{
    console.log(data);
    this.reservations=data;
  });
}
addReservation() {
  this.router.navigate(['addRes']); // Naviguer vers la page d'ajout
}
showDetails(idReservation: any) {
  this.router.navigate(['/detailRes', idReservation]);}

deleteReservation(reser: Reservations) {
  this.sReservation.deleteReservation(res).subscribe((data) => {
    console.log(data);
  }); }
}
<button class="btn btn-success btn-sm action-button" (click)="addReservation()">
  Ajouter réservation
</button>
<table class="table table-striped table-bordered">
  <thead>
    <tr>
      <th>ID</th>
      <th>Année Universitaire</th>
      <th>Est Valide ?</th>
      <th>Commentaire</th>
      <th>Actions</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let res of reservations">
      <td>{{ res.idReservation }}</td>
      <td>{{ res.anneeUniversite | date:'yyyy-MM-dd' }}</td>
      <td>
        <span class="badge badge-info" style="color: black;">{{ res.estValide ? 'Oui' : 'Non' }}</span>
      </td>
      <td>{{ res.commentaire }}</td>
      <td>
        <button class="btn btn-primary btn-sm action-button" (click)="showDetails(res.idReservation)" >
          <i class="fas fa-info-circle"></i> Détails
        </button>
        <button class="btn btn-warning btn-sm action-button" >
          <i class="fas fa-edit"></i> Modifier
        </button>
        <button class="btn btn-danger btn-sm action-button" (click)="deleteReservation(res)">
          <i class="fas fa-trash-alt"></i> Supprimer
        </button>
      </td>
    </tr>
  </tbody>
</table>
