import { Component } from '@angular/core';

@Component({
  selector: 'app-update-reservation',
  templateUrl: './update-reservation.component.html',
  styleUrls: ['./update-reservation.component.scss']
})
export class UpdateReservationComponent {
  res:any;
  idChambre:any;
  constructor(private sChambre:ChambreService, private ac:ActivatedRoute) {
    this.idChambre = this.ac.snapshot.params['idChambre'];

  }
  ngOnInit() {
    this.sChambre.getChambreById(this.idChambre).subscribe(data => {
      this.ch = data;
    });
  }
  update() {
    this.sChambre.updateChambre(this.idChambre,this.ch).subscribe();
  console.log(this.ch);}
}
