import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ReservationsService } from 'src/app/services/reservations.service';

@Component({
  selector: 'app-detail-reservation',
  templateUrl: './detail-reservation.component.html',
  styleUrls: ['./detail-reservation.component.scss']
})
export class DetailReservationComponent implements OnInit {
  reservation: any;
  constructor(
    private route: ActivatedRoute,
    private sReservation: ReservationsService
  ) {}
  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      const idParam = params.get('idReservation');
      if (idParam !== null) {
        //const resId = +idParam;//persister pour convertir l'id en number
        this.sReservation.getReservationById(idParam).subscribe(reservation => {
          this.reservation = reservation;
        });
      } else {

      }
    });
  }
  addReservation() {
    this.router.navigate(['addRes']); // Naviguer vers la page d'ajout
  }
}
<div class="container mt-4">
  <div *ngIf="reservation" class="card">
    <div class="card-body">
      <h5 class="card-title text-center">Informations de la réservation</h5>
      <div class="row">
        <div class="col-md-6">
          <p class="card-text"><strong>ID de la Réservation:</strong> {{ reservation.idReservation }}</p>
        </div>
        <div class="col-md-6">
          <p class="card-text"><strong>Année Universitaire:</strong> {{ reservation.anneeUniversite | date:'yyyy-MM-dd' }}</p>
        </div>
        <div class="col-md-6">
          <p class="card-text"><strong>Est Valide:</strong> {{ reservation.estValide ? 'Oui' : 'Non' }}</p>
        </div>
        <div class="col-md-6">
          <p class="card-text"><strong>Commentaire:</strong> {{ reservation.commentaire }}</p>
        </div>
      </div>
      <div class="text-center">
        <a  class="btn btn-primary" (click)="addRes()">Réserver</a>
      </div>
    </div>
  </div>
</div>
