import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ReservationsService } from 'src/app/services/reservations.service';

@Component({
  selector: 'app-detail-reservation',
  templateUrl: './detail-reservation.component.html',
  styleUrls: ['./detail-reservation.component.scss']
})
export class DetailReservationComponent implements OnInit {
  reservation: any;
  constructor(
    private route: ActivatedRoute,
    private sReservation: ReservationsService
  ) {}
  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      const idParam = params.get('idReservation');
      if (idParam !== null) {
        const resId = +idParam;
        this.sReservation.getReservationById(resId).subscribe(reservation => {
          this.reservation = reservation;
        });
      } else {

      }
    });
  }
}
<div class="container mt-4">
  <div *ngIf="reservation" class="card">
    <div class="card-body">
      <h5 class="card-title text-center">Informations de la reservation</h5>
      <div class="row">
        <div class="col-md-6">
          <p class="card-text"><strong>ID de la Chambre:</strong> {{ chambre.idChambre }}</p>
        </div>
        <div class="col-md-6">
          <p class="card-text"><strong>Numéro de Chambre:</strong> {{ chambre.numeroChambre }}</p>
        </div>
        <div class="col-md-6">
          <p class="card-text"><strong>type chambre:</strong> {{ chambre.typeChamber }}</p>
        </div>
      </div>
      <div class="text-center">
        <a href="#" class="btn btn-primary">Réserver</a>
      </div>
    </div>
  </div>
</div>
