import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ChambreService } from 'src/app/services/chambre.service';

@Component({
  selector: 'app-update-chambre',
  templateUrl: './update-chambre.component.html',
  styleUrls: ['./update-chambre.component.scss']
})
export class UpdateChambreComponent {
ch:any;
idChambre:any;
constructor(private sChambre:ChambreService, private ac:ActivatedRoute) {
  this.idChambre = this.ac.snapshot.params['idChambre'];

}
update() {
  this.sChambre.updateChambre(this.idChambre,this.ch).subscribe();
console.log(this.ch);}
}
<form (ngSubmit)="update()">
  <div class="form-group">
    <label for="id">ID</label>
    <div class="form-group">
      <input type="text" id="id" name="id" [(ngModel)]="pr.id" [disabled]="true" class="form-control">
    </div>
      </div>
  <div class="form-group">
    <label for="description">Description:</label>
    <input type="text" id="description" name="description" [(ngModel)]="pr.description" class="form-control">
  </div>
  <div class="form-group">
    <label for="prixUnitaire">Prix Unitaire:</label>
    <input type="text" id="prixUnitaire" name="prixUnitaire" [(ngModel)]="pr.prixUnitaire" class="form-control">
  </div>
  <div class="form-group">
    <label for="tauxTVA">Taux TVA:</label>
    <input type="text" id="tauxTVA" name="tauxTVA" [(ngModel)]="pr.tauxTVA" class="form-control">
  </div>
<button type="submit" > save</button>
 </form>
