  <div class="form-group">
    <label for="numeroChambre">numeroChambre:</label>
    <input
      type="number"
      id="numeroChambre"
      formControlName="numeroChambre"
      class="form-control"
    />
  </div>
  <button type="submit" class="btn btn-primary">Ajouter Produit</button>
</form>
