import { Component } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-add-reservation',
  templateUrl: './add-reservation.component.html',
  styleUrls: ['./add-reservation.component.scss']
})
export class AddReservationComponent {
  addRes: FormGroup;


  constructor(private fb: FormBuilder, private S: ChambreService) {
    this.addCh = this.fb.group({
      numeroChambre: ['', Validators.required],
      typeChambre: ['', Validators.required], // Initialisez à null
    });
  }

  onSubmit() {
    if (this.addCh.valid) {
      const ch = this.addCh.value;
      this.chService.addChambre(ch).subscribe((data) => {
        console.log(data);
        alert('Chambre ajoutée avec succès');
      });
    }
  }
}
