import { ActivatedRoute } from '@angular/router';
import { Chambre } from './../../model/Chambre';
import { Component, OnInit } from '@angular/core';
import { ChambreService } from 'src/app/services/chambre.service';

@Component({
  selector: 'app-detail-chambre',
  templateUrl: './detail-chambre.component.html',
  styleUrls: ['./detail-chambre.component.scss'],
})
export class DetailChambreComponent implements OnInit {
  chambre: any;
  constructor(
    private route: ActivatedRoute,
    private sChambre: ChambreService
  ) {}
  ngOnInit(): void {
    this.route.paramMap.subscribe((params) => {
      const idParam = params.get('id');

      const chId=+idParam;

      this.sChambre.getChambreById(chId).subscribe((data) => {
        this.chambre = data;
      });
    });
  }
}
<div class="container">
  <div class="card">
    <div class="card-body">
      <h2 class="card-title">ID chambre :{{ chambre.id }}</h2>
      <p class="card-text">Numero Chambre : {{ chambre.numeroChambre }} €</p>
    </div>
  </div>
</div>
