import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { ReservationsService } from 'src/app/services/reservations.service';

@Component({
  selector: 'app-update-reservation',
  templateUrl: './update-reservation.component.html',
  styleUrls: ['./update-reservation.component.scss']
})
export class UpdateReservationComponent {
  reservation:any;
  idReservation:any;
  constructor(private sReservation:ReservationsService, private ac:ActivatedRoute) {
    this.idReservation = this.ac.snapshot.params['idReservation'];

  }
  ngOnInit() {
    this.sReservation.getReservationById(this.idReservation).subscribe(data => {
      this.reservation = data;
      console.log(this.reservation);
    });
  }
  updateReservation() {
    this.sReservation.updateReservation(this.idReservation,this.reservation).subscribe();
  console.log(this.reservation);}


}
<form (ngSubmit)="updateReservation()">
  <div class="form-group">
    <label for="idReservation">ID de la Réservation</label>
    <div class="form-group">
      <input type="text" id="idReservation" name="idReservation" [(ngModel)]="reservation.idReservation" [disabled]="true" class="form-control">
    </div>
  </div>
  <div class="form-group">
    <label for="anneeUniversite">Année Universitaire</label>
    <input type="datetime" id="anneeUniversite" name="anneeUniversite" [(ngModel)]="reservation.anneeUniversite" class="form-control">
  </div>

  <div class="form-group">
    <label for="estValide">Est Valide</label>
    <input type="radio" id="estValideOui" name="estValide" value="true" [(ngModel)]="reservation.estValide" class="form-check-input">
    <label for="estValideOui" class="form-check-label">Oui</label>
    <input type="radio" id="estValideNon" name="estValide" value="false" [(ngModel)]="reservation.estValide" class="form-check-input">
    <label for="estValideNon" class="form-check-label">Non</label>
  </div>
  <div class="form-group">
    <label for="commentaire">Commentaire</label>
    <textarea id="commentaire" name="commentaire" [(ngModel)]="reservation.commentaire" rows="4" class="form-control"></textarea>
  </div>
  <button type="submit">Sauvegarder</button>
</form>
