import { ActivatedRoute, Router } from '@angular/router';
import { Chambre } from '../../model/Chambre';
import { Component, OnInit } from '@angular/core';
import { ChambreService } from 'src/app/services/chambre.service';

@Component({
  selector: 'app-detail-chambre',
  templateUrl: './detail-chambre.component.html',
  styleUrls: ['./detail-chambre.component.scss'],
})
export class DetailChambreComponent implements OnInit {
  chambre: any;
  idChambre:any;
  chambres: Chambre[] = [];
  selectedchambres!: number;
    constructor(
    private route: ActivatedRoute,
    private sChambre: ChambreService, private router:Router
  ) {}
  ngOnInit() {
    this.route.paramMap.subscribe(params => {
      const idParam = params.get('idChambre');
      if (idParam !== null) {
        const chambreId = +idParam;
        this.sChambre.getChambreById(chambreId).subscribe(chambre => {
          this.chambre = chambre;
        });
      } else {

      }
    });
  }
  editCh(idChambre: any) {
    this.router.navigate(['/gestion-chambre/updateCh', idChambre]);
  }

}
<div class="container mt-4">
  <div *ngIf="chambre" class="card">
    <div class="card-body">
      <h5 class="card-title text-center">Informations de la Chambre</h5>
      <div class="row">
        <div class="col-md-6">
          <p class="card-text"><strong>ID de la Chambre:</strong> {{ chambre.idChambre }}</p>
        </div>
        <div class="col-md-6">
          <p class="card-text"><strong>Numéro de Chambre:</strong> {{ chambre.numeroChambre }}</p>
        </div>
        <div class="col-md-6">
          <p class="card-text"><strong>type chambre:</strong> {{ chambre.typeC }}</p>
        </div>
      </div>

    </div>
  </div>
</div>
