import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ChambreService } from 'src/app/services/chambre.service';
import { TypeChambre } from 'src/app/model/typeChambre';

@Component({
  selector: 'app-add-chambre',
  templateUrl: './add-chambre.component.html',
  styleUrls: ['./add-chambre.component.scss'],
})
export class AddChambreComponent {
  addCh: FormGroup;
  typeChambreEnum = TypeChambre; // Enum pour le champ de sélection

  constructor(private fb: FormBuilder, private chService: ChambreService) {
    this.addCh = this.fb.group({
      numeroChambre: ['', Validators.required],
      typeChambre: [null, Validators.required], // Initialisez à null
    });
  }

  onSubmit() {
    if (this.addCh.valid) {
      const ch = this.addCh.value;
      this.chService.addChambre(ch).subscribe((data) => {
        console.log(data);
        alert('Chambre ajoutée avec succès');
      });
    }
  }
}
<form [formGroup]="addCh" (ngSubmit)="onSubmit()" class="form-container">
  <div class="form-group">
    <label for="numeroChambre" class="form-label">Numéro de Chambre:</label>
    <input
      type="number"
      id="numeroChambre"
      formControlName="numeroChambre"
      class="form-control"
    />
  </div>
  <div class="form-group">
    <label for="typeChambre" class="form-label">Type de Chambre:</label>
    <select id="typeChambre" formControlName="typeChambre" class="form-control">
      <option *ngFor="let type of typeChambre" [value]="type">{{ type }}</option>
    </select>
  </div>

  <button type="submit" class="btn btn-primary">Ajouter Chambre</button>
</form>
